mapper.delete:
DELETE FROM student_course
WHERE student_id = #{student_id}::integer
    AND status = #{status}

mapper.select:
WITH completed_courses AS ( SELECT course_code FROM student_course WHERE student_id = #{student_id}::integer AND status = 'X' ), satisfied_groups AS ( SELECT DISTINCT cg.target_course_code FROM course_condition_group cg WHERE NOT EXISTS ( SELECT 1 FROM course_condition_item ci WHERE ci.condition_group_id = cg.condition_group_id AND ci.required_course_code NOT IN (SELECT course_code FROM completed_courses) ) ) SELECT c.course_id, c.course_code, c.course_name, substring(c.period,1,1) as period, c.room, c.student_count, c.max_seats FROM course c WHERE substring(c.period,1,1) = #{period}
AND ( NOT EXISTS (SELECT 1 FROM course_grade_condition g WHERE g.course_code = c.course_code) OR EXISTS (SELECT 1 FROM course_grade_condition g WHERE g.course_code = c.course_code AND #{grade}::integer BETWEEN g.min_grade AND g.max_grade) )
AND ( NOT EXISTS (SELECT 1 FROM course_condition_group cg WHERE cg.target_course_code = c.course_code) OR c.course_code IN (SELECT target_course_code FROM satisfied_groups) ) ORDER BY c.course_code, course_id

mapper.update:
UPDATE course
SET
    student_count = #{student_count},
    max_seats = #{max_seats}
WHERE course_id = #{course_id}
