mapper.select:
SELECT c.course_seq, c.course_code, c.course_name, substring(c.period,1,1) as period, c.room, c.student_count, c.max_seats FROM course c WHERE substring(c.period,1,1) = #{period}
AND ( NOT EXISTS ( SELECT 1 FROM course_grade_condition g WHERE g.course_id = c.course_code ) OR EXISTS ( SELECT 1 FROM course_grade_condition g WHERE g.course_id = c.course_code AND #{grade}::integer BETWEEN g.min_grade AND g.max_grade ) )
AND ( NOT EXISTS ( SELECT 1 FROM course_condition_group cg WHERE cg.target_course_code = c.course_code ) OR EXISTS ( SELECT 1 FROM course_condition_group cg WHERE cg.target_course_code = c.course_code AND NOT EXISTS ( SELECT 1 FROM course_condition_item ci WHERE ci.group_id = cg.id AND NOT EXISTS ( SELECT 1 FROM student_course sc WHERE sc.student_id = #{student_id}::integer AND sc.course_id = ci.required_course_code AND sc.status = 'X' ) ) ) ) ORDER BY c.course_code
